outbound.set-body:
@{
            var body = context.Response.Body.As();
            var response = new JObject();
            response.Add(new JProperty("status", body["status"]));

            if(body["status"].ToObject() == "OK") {
                response.Add(new JProperty("result", body["candidates"][0]));
            } else {
                response.Add(new JProperty("result", null));
            }

            return response.ToString();
        }
